import Axios from 'axios';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { Helmet } from 'react-helmet-async';
import { useContext, useEffect, useState } from 'react';
import { Store } from '../Store';
import { toast } from 'react-toastify';
import { getError } from '../utils';

export default function SigninScreen() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const redirectInUrl = new URLSearchParams(search).get('redirect');
  const redirect = redirectInUrl ? redirectInUrl : '/';

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;

   function handleEmailInput(e){
      e.preventDefault()
      setEmail(e.target.value)
  }
  function handlePasswordInput(e){
      e.preventDefault()
      setPassword(e.target.value)
  }

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const { data } = await Axios.post('/api/users/signin', {
        email,
        password,
      });
      ctxDispatch({ type: 'USER_SIGNIN', payload: data });
      localStorage.setItem('userInfo', JSON.stringify(data));
      navigate(redirect || '/');
    } catch (err) {
      toast.error(getError(err));
    }
  };

  useEffect(() => {
    if (userInfo) {
      navigate(redirect);
    }
  }, [navigate, redirect, userInfo]);

  return (
    <Container className="small-container">
      <Helmet>
        <title>Sign In</title>
      </Helmet><div className='App'>

           <header>
            <form style={{padding: '10px', paddingBottom: '100px'}}>
                <h4>Login Form</h4>
                <br></br>
           <br/>
           <label>Email</label> <br/><br/>
           <input type="text" value={email} onChange={handleEmailInput} />
           <br/><br />
           <label>Password</label> <br/><br/> 
           <input type='password' value={password} onChange={handlePasswordInput} />
           <br/> <br />
           <button className='logBtn' onClick={submitHandler}>Login</button>
           <br/> <br />
           <p>ForgotPassword click <span></span>
           <Link to="/forgotPassword">ForgotPassword</Link></p>
           <br/>
           <div>
            <h4>New customer?{' '} 
             <span></span>   
             <Link to={`/signup?redirect=${redirect}`}>Create your account</Link>
             </h4>
           </div>
            </form>
           </header>

          </div>
    </Container>
  );
}
