import {  useNavigate } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import { Helmet } from 'react-helmet-async';
import { useState } from 'react';


export default function Contact() {
  const navigate = useNavigate();
 
  const [name, setName] = useState('');
  const [nameError, setNameError] = useState('');
  const [email, setEmail] = useState('');
  const [emailError, setEmailError] = useState('');
  const [text, setText] = useState('');
  const [message, setMessage] = useState('');
 const nameRegExp  = /^[A-z]{3,23}$/;
 const emailRegExp = /\S+@\S+\.\S+/;
  function handleNameInput(e){
    e.preventDefault()
    if(e.target.value === ""){
      setNameError("name must be filled");
    }else if((e.target.value).length<3 || (e.target.value).length>23){
      setNameError("name must be min 3 and max 23 characters")
    } else if(!new RegExp(nameRegExp).test(e.target.value)){
      setNameError("name can contain only [a-z]")
    } else{
      setName(e.target.value);
      setNameError("")
    }
   
}
  function handleEmailInput(e){
    e.preventDefault()
    if(e.target.value === ""){
      setEmailError("email must be filled");
    } else if(!new RegExp(emailRegExp).test(e.target.value)){
      setEmailError("email format is not valid")
    } else{
       setEmail(e.target.value)
       setEmailError("")
    }
   
}
function handleTextInput(e){
    e.preventDefault();
}

  const submitHandler = async (e) => {
    e.preventDefault()
    setName("")
    setEmail("")
    setText("")
    setMessage("We will contact you soon")
    navigate('/');
}

const errorHandler = (e) => {
e.preventDefault();
setMessage("All fields must be filled");
}

if(name!=="" && email!=="" && text!=="" ){
  return (
    <Container className="small-container App">
      <Helmet>
        <title>Contact</title>
      </Helmet>
      <section class="vh-100" style="background-color: #2779e2;">
  <div class="container h-100">
    <div class="row d-flex justify-content-center align-items-center h-100">
      <div class="col-xl-9">
        <h1 class="text-white mb-4">Contact FOrm</h1>
        <h4 style={{color:'red'}}> {message} </h4>
        <div class="card" style="border-radius: 15px;">
          <div class="card-body">
            <div class="row align-items-center pt-4 pb-3">
              <div class="col-md-3 ps-5">
                <h6 class="mb-0">Full name</h6>
              </div>
              <div class="col-md-9 pe-5">
                <input type="text" class="form-control form-control-lg" placeholder='Enter name' onChange={handleNameInput}/>
              </div>
              <h4 style={{color:'red'}}> {nameError} </h4>
            </div>
            <hr class="mx-n3"/>
            <div class="row align-items-center py-3">
              <div class="col-md-3 ps-5">
                <h6 class="mb-0">Email address</h6>
              </div>
              <div class="col-md-9 pe-5">
                <input type="email" class="form-control form-control-lg" placeholder="example@example.com" onChange={handleEmailInput} />
              </div>
              <h4 style={{color:'red'}}> {emailError} </h4>
            </div>
            <hr class="mx-n3"/>
            <div class="row align-items-center py-3">
              <div class="col-md-3 ps-5">
                <h6 class="mb-0">Message</h6>
              </div>
              <div class="col-md-9 pe-5">
                <textarea class="form-control" rows="3" cols = "50" placeholder="Enter Message" onChange={handleTextInput}></textarea>
              </div>
            </div>
            <hr class="mx-n3"/>
            <div class="px-5 py-4">
              <button type="submit" class="btn btn-primary btn-lg" onClick={submitHandler}>Send Message</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
    </Container>
  );
} else{
  return (
    <Container className="small-container App">
      <Helmet>
        <title>Contact</title>
      </Helmet>
      <section class="vh-100" style="background-color: #2779e2;">
  <div class="container h-100">
    <div class="row d-flex justify-content-center align-items-center h-100">
      <div class="col-xl-9">
        <h1 class="text-white mb-4">Contact FOrm</h1>
        <h4 style={{color:'red'}}> {message} </h4>
        <div class="card" style="border-radius: 15px;">
          <div class="card-body">
            <div class="row align-items-center pt-4 pb-3">
              <div class="col-md-3 ps-5">
                <h6 class="mb-0">Full name</h6>
              </div>
              <div class="col-md-9 pe-5">
                <input type="text" class="form-control form-control-lg" placeholder='Enter name' onChange={handleNameInput}/>
              </div>
              <h4 style={{color:'red'}}> {nameError} </h4>
            </div>
            <hr class="mx-n3"/>
            <div class="row align-items-center py-3">
              <div class="col-md-3 ps-5">
                <h6 class="mb-0">Email address</h6>
              </div>
              <div class="col-md-9 pe-5">
                <input type="email" class="form-control form-control-lg" placeholder="example@example.com" onChange={handleEmailInput} />
              </div>
              <h4 style={{color:'red'}}> {emailError} </h4>
            </div>
            <hr class="mx-n3"/>
            <div class="row align-items-center py-3">
              <div class="col-md-3 ps-5">
                <h6 class="mb-0">Message</h6>
              </div>
              <div class="col-md-9 pe-5">
                <textarea class="form-control" rows="3" cols = "50" placeholder="Enter Message" onChange={handleTextInput}></textarea>
              </div>
            </div>
            <hr class="mx-n3"/>
            <div class="px-5 py-4">
              <button type="submit" class="btn btn-primary btn-lg" onClick={errorHandler}>Send Message</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
    </Container>
  );
}
  
}
