import Axios from 'axios';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import { Helmet } from 'react-helmet-async';
import { useContext, useEffect, useState } from 'react';
import { Store } from '../Store';
import { toast } from 'react-toastify';
import { getError } from '../utils';


export default function SignupScreen() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const redirectInUrl = new URLSearchParams(search).get('redirect');
  const redirect = redirectInUrl ? redirectInUrl : '/';

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;
 const nameRegEx  = /^[A-z][A-z0-9-_]{3,23}$/;
 const emailRegExp = /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/g;
 const pwdRegExp =/^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;
  function handleNameInput(e){
    e.preventDefault()
    setName(e.target.value);

}
  function handleEmailInput(e){
    e.preventDefault()
    setEmail(e.target.value)
}
function handlePasswordInput(e){
    e.preventDefault()
    setPassword(e.target.value)
}
function handleConfirmPasswordInput(e){
  e.preventDefault()
  setConfirmPassword(e.target.value)
}

  const submitHandler = async (e) => {
    e.preventDefault();
   if(name === "" || email === "" || password === "" || confirmPassword === ""){
     setMessage("fields must be filled out.")
   } else if(!nameRegEx.test(name) || !emailRegExp.test(email) || !pwdRegExp.test(password)){
    setError(true);
    return;
    }
    if (password !== confirmPassword) {
      toast.error('Passwords do not match');
      return;
    }
    try {
      const { data } = await Axios.post('/api/users/signup', {
        name,
        email,
        password,
      });
      ctxDispatch({ type: 'USER_SIGNIN', payload: data });
      localStorage.setItem('userInfo', JSON.stringify(data));
      navigate(redirect || '/');
    } catch (err) {
      toast.error(getError(err));
    }
  };

  useEffect(() => {
    if (userInfo) {
      navigate(redirect);
    }
  }, [navigate, redirect, userInfo]);

  return (
    <Container className="small-container App">
      <Helmet>
        <title>Sign Up</title>
      </Helmet>
      <section class="vh-100 App" style={{backgroundColor: '#eee'}}>
  <div class="container h-100">
    <div class="row d-flex justify-content-center align-items-center h-100 w-100">
      <div class="col-lg-12 col-xl-11">
        <div class="text-black" style={{ paddingLeft:'75px', width:'1100px'}}>
          <div class="p-md-5">
            <div class="row justify-content-center">
              <div class="col-md-10 col-lg-6 col-xl-5 order-2 order-lg-1">
                <p class="text-center h1 fw-bold mb-5 mx-1 mx-md-4 mt-4">Sign up</p>
                <form class="mx-1 mx-md-4">
                  <div class="d-flex flex-row align-items-center mb-4">
                    <i class="fas fa-user fa-lg me-3 fa-fw"></i>
                    <div class="form-outline flex-fill mb-0">
                      <input type="text" id="form3Example1c" class="form-control"onChange={handleNameInput} autoComplete ='off' controlId="name" placeholder='your name' pattern='' required/>
                      {error && 
                      <label>name format is invalid</label>}
                      {message}
                    </div>
                  </div>

                  <div class="d-flex flex-row align-items-center mb-4">
                    <i class="fas fa-envelope fa-lg me-3 fa-fw"></i>
                    <div class="form-outline flex-fill mb-0">
                      <input type="email" id="form3Example3c" class="form-control" onChange={handleEmailInput} controlId="email" placeholder='your email' required/>
                      {error && 
                      <label>Email format is invalid</label>}
                       {message}
                    </div>
                  </div>

                  <div class="d-flex flex-row align-items-center mb-4">
                    <i class="fas fa-lock fa-lg me-3 fa-fw"></i>
                    <div class="form-outline flex-fill mb-0">
                      <input type="password" id="form3Example4c" class="form-control" onChange={handlePasswordInput}controlId="password" placeholder='your password' required/>
                      {error && 
                      <label>Password must be 8 characters include one uppercase, one number and one special character</label>}
                       {message}
                    </div>
                  </div>

                  <div class="d-flex flex-row align-items-center mb-4">
                    <i class="fas fa-key fa-lg me-3 fa-fw"></i>
                    <div class="form-outline flex-fill mb-0">
                      <input type="password" id="form3Example4cd" class="form-control" onChange={handleConfirmPasswordInput}controlId="password" placeholder='re enter password' required/>
                      {error && 
                      <label>Field can't be empty</label>}
                    </div>
                  </div>

                  <div class="form-check d-flex justify-content-center mb-5">
                    <label class="form-check-label" for="form2Example3">
                      Already Have an account {''} <Link to={`/signin?redirect=${redirect}`}>Sign-In</Link>
                    </label>
                  </div>

                  <div class="d-flex justify-content-center mx-4 mb-3 mb-lg-4">
                    <button type="button" class="btn btn-primary btn-lg" onClick={submitHandler}>Register</button>
                  </div>

                </form>

              </div>
              <div class="col-md-10 col-lg-6 col-xl-7 d-flex align-items-center order-1 order-lg-2">
                <img src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-registration/draw1.webp"
                  class="img-fluid" alt="Sample image"/>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
    </Container>
  );
}
