//https://www.pngkey.com/download/u2w7u2o0o0e6e6i1_contact-me-contact-button-for-website/  image for contact us
import {useState} from 'react'
import { Helmet } from 'react-helmet-async'

function Contact(){
    
   const [message, setMessage] = useState('')
   const [username, setName] = useState('')
  const [nameError, setNameError] = useState('');
   const nameRegExp  = /^[A-z]{3,23}$/;
 const emailRegExp = /\S+@\S+\.\S+/;
   const [email, setEmail] = useState('')
   const [emailError, setEmailError] = useState('');
   const [text, setText] = useState('')

   function handleUsernameInput(e){
      e.preventDefault()
      if(e.target.value === ""){
        setNameError("name must be filled");
      }else if((e.target.value).length<3 || (e.target.value).length>23){
        setNameError("name must be min 3 and max 23 characters")
      } else if(!new RegExp(nameRegExp).test(e.target.value)){
        setNameError("name can contain only [a-z]")
      } else{
        setName(e.target.value);
        setNameError("")
      }
  }
  function handleEmailInput(e){
      e.preventDefault()
      if(e.target.value === ""){
        setEmailError("email must be filled");
      } else if(!new RegExp(emailRegExp).test(e.target.value)){
        setEmailError("email format is not valid")
      } else{
         setEmail(e.target.value)
         setEmailError("")
      }
  }
  function handleTextInput(e){
    e.preventDefault()
    setText(e.target.value)
}
 function handleSubmit(e){
      e.preventDefault()
      setName("")
      setEmail("")
      setText("")
      setMessage("We will contact you soon")
  }
  function handleError(e){
    e.preventDefault()
    setName("")
    setEmail("")
    setText("")
    setMessage("Please enter all the details")
}
  if(!username === "" && !email === "" && !text === ""){
      return(
        <div className='App'>
          <Helmet><title>Contact</title></Helmet>  
           <header>
            <form style={{padding: '10px', paddingBottom: '100px'}}>
                <h4>Contact Form</h4>
                <br></br>
                <h4>{message}</h4>
           <label>UserName</label> <br/><br/>
           <input type="text" value={username} onChange={handleUsernameInput} />
           <h4 style={{color:'red'}}> {nameError} </h4>    
           <br/><br />
           <label>Email</label> <br/><br/> 
           <input type='email' value={email} onChange={handleEmailInput} />
           <h4 style={{color:'red'}}> {emailError} </h4>    
           <br/> <br /> 
           <label>Message</label> <br/><br/>
           <textarea rows = "5" cols = "50" name = "description" onChange={handleTextInput}>
         </textarea> <br/><br/>
           <br/> <br />
           <button onClick={handleSubmit}>Submit</button>
           <br/> <br />
            </form>
           </header>
           </div>
         
      )
  } else{
      return(
          <div className='App'>
            <Helmet><title>Contact</title></Helmet>
           <header>
            <form style={{padding: '10px', paddingBottom: '100px'}}>
                <h1>Contact Form</h1>
                <h4 style={{color:'red'}}> {message} </h4>
           <br/>
           <label>UserName</label> <br/><br/>
           <input type="text" value={username} onChange={handleUsernameInput} />
           <h4 style={{color:'red'}}> {nameError} </h4>    
           <br/><br />
           <label>Email</label> <br/><br/> 
           <input type='email' value={email} onChange={handleEmailInput} />  
            <h4 style={{color:'red'}}> {emailError} </h4>    
           <br/> <br />
         <label>Message</label> <br/><br/>
           <textarea rows = "5" cols = "50" name = "description" onChange={handleTextInput}>
         </textarea> <br/><br/>
           <button onClick={handleError}>Submit</button>
           <br/> <br />
            </form>
           </header>
           </div>
          )
  }
 }
 export default Contact;
